function DominoDbError(message, code, cause) {
  this.message = message;
  this.code = code;
  this.cause = cause;
  this.stack = new Error().stack;
}

DominoDbError.prototype = new Error();
DominoDbError.prototype.name = 'DominoDbError';

module.exports = DominoDbError;
